   1→import {
   2→  CustomerCreatedEvent,
   3→  CustomerUpdatedEvent,
   4→  EventEntity,
   5→  EventName,
   6→  SubscriptionCanceledEvent,
   7→  SubscriptionCreatedEvent,
   8→  SubscriptionUpdatedEvent,
   9→  TransactionCompletedEvent,
  10→} from "@paddle/paddle-node-sdk";
  11→import { createServiceSupabase } from "@/lib/supabase/server";
  12→import { PADDLE_PRICE_TIERS } from "./pricing-config";
  13→import { getPaddleInstance } from "./get-paddle-instance";
  14→
  15→// Custom data type for credit purchases
  16→interface CreditPurchaseCustomData {
  17→  type: "credit";
  18→  quantity: string;
  19→}
  20→
  21→export class ProcessWebhook {
  22→  async processEvent(eventData: EventEntity) {
  23→    console.log(`Processing Paddle webhook event: ${eventData.eventType}`);
  24→
  25→    switch (eventData.eventType) {
  26→      case EventName.SubscriptionCreated:
  27→      case EventName.SubscriptionUpdated:
  28→      case EventName.SubscriptionCanceled:
  29→        await this.updateSubscriptionData(eventData);
  30→        break;
  31→      case EventName.CustomerCreated:
  32→      case EventName.CustomerUpdated:
  33→        await this.updateCustomerData(eventData);
  34→        break;
  35→      case EventName.TransactionCompleted:
  36→        await this.handleTransactionCompleted(eventData);
  37→        break;
  38→      default:
  39→        console.log(`Unhandled event type: ${eventData.eventType}`);
  40→    }
  41→  }
  42→
  43→  private async updateSubscriptionData(
  44→    eventData: SubscriptionCreatedEvent | SubscriptionUpdatedEvent | SubscriptionCanceledEvent
  45→  ) {
  46→    const supabase = createServiceSupabase();
  47→
  48→    try {
  49→      const eventType = eventData.eventType.includes("created")
  50→        ? "created"
  51→        : eventData.eventType.includes("canceled")
  52→        ? "canceled"
  53→        : "updated";
  54→
  55→      console.log(
  56→        `[subscription.${eventType}] Processing subscription ${eventData.data.id} for customer ${
  57→          eventData.data.customerId
  58→        }`
  59→      );
  60→
  61→      // customer_id로 customers 테이블에서 user_id 조회 (없을 수도 있음 - customer.created가 먼저 와야 함)
  62→      const { data: customerData } = await supabase
  63→        .from("customers")
  64→        .select("user_id")
  65→        .eq("customer_id", eventData.data.customerId)
  66→        .single();
  67→
  68→      console.log(
  69→        `[subscription] Customer ${
  70→          eventData.data.customerId
  71→        } exists: ${!!customerData}, user_id: ${
  72→          customerData?.user_id || "not linked yet"
  73→        }`
  74→      );
  75→
  76→      // IMPORTANT: customer가 없어도 계속 진행
  77→      // customer.created 이벤트가 나중에 와서 customer를 생성하고 subscription을 연결할 것임
  78→      // transaction.completed에서 최종 연결이 이루어짐
  79→
  80→      // 2. Paddle Price ID로부터 플랜 결정
  81→      const priceId = eventData.data.items[0].price?.id;
  82→      const planName = this.determinePlanFromPriceId(priceId);
  83→
  84→      // 3. subscription_plans 테이블에서 plan_id 조회
  85→      const { data: planData, error: planError } = await supabase
  86→        .from("subscription_plans")
  87→        .select("id")
  88→        .eq("name", planName)
  89→        .eq("is_active", true)
  90→        .single();
  91→
  92→      if (planError || !planData) {
  93→        console.error(`Failed to find plan for name: ${planName}`, planError);
  94→        return;
  95→      }
  96→
  97→      // 4. 기존 subscriptions 테이블에 Paddle 구독 정보 upsert
  98→
  99→      // Log detailed subscription data for debugging
 100→      console.log('[subscription] Paddle data:', JSON.stringify({
 101→        status: eventData.data.status,
 102→        scheduled_change: eventData.data.scheduled_change,
 103→        next_billed_at: eventData.data.next_billed_at,
 104→        current_billing_period: eventData.data.currentBillingPeriod,
 105→      }, null, 2));
 106→
 107→      // Calculate ends_at from scheduled_change or next_billed_at
 108→      let endsAt: string | null = null;
 109→      let finalStatus = this.mapPaddleStatus(eventData.data.status);
 110→      
 111→      // Priority 1: If subscription has scheduled cancellation, use effective_at
 112→      if (eventData.data.scheduled_change?.action === 'cancel') {
 113→        endsAt = eventData.data.scheduled_change.effective_at || null;
 114→        console.log(`[subscription] Scheduled cancellation detected, ends_at: ${endsAt}`);
 115→
 116→        // Check if subscription has already expired
 117→        if (endsAt && new Date(endsAt) < new Date()) {
 118→          finalStatus = 'expired';
 119→          console.log(`[subscription] Subscription already expired, changing status to 'expired'`);
 120→        }
 121→      }
 122→      // Priority 2: Use next_billed_at for active recurring subscriptions
 123→      else if (eventData.data.next_billed_at) {
 124→        endsAt = eventData.data.next_billed_at;
 125→        console.log(`[subscription] Setting ends_at to next billing date: ${endsAt}`);
 126→      }
 127→      // Priority 3: Fallback to current billing period end date
 128→      else if (eventData.data.currentBillingPeriod?.endsAt) {
 129→        endsAt = eventData.data.currentBillingPeriod.endsAt;
 130→        console.log(`[subscription] Using current billing period end date: ${endsAt}`);
 131→      }
 132→
 133→      // If still no ends_at and status is canceled, log warning
 134→      if (!endsAt && (eventData.data.status === 'canceled' || finalStatus === 'canceled')) {
 135→        console.warn(`[subscription] ⚠️ Canceled subscription has no ends_at date - Paddle data may be incomplete`);
 136→      }
 137→
 138→      let subscriptionId: string;
 139→
 140→      // user_id가 있으면 기존 subscription 레코드를 업데이트, 없으면 새로 생성
 141→      if (customerData?.user_id) {
 142→        console.log(`[subscription] User ${customerData.user_id} exists, checking for existing subscription...`);
 143→
 144→        // 이 user의 기존 subscription 찾기 (plan_id 포함)
 145→        const { data: existingUserSub } = await supabase
 146→          .from("subscriptions")
 147→          .select("id, plan_id")
 148→          .eq("user_id", customerData.user_id)
 149→          .single();
 150→
 151→        if (existingUserSub) {
 152→          // 기존 subscription 업데이트 (Free → Pro 등)
 153→          console.log(`[subscription] Updating existing subscription ${existingUserSub.id} to ${planName}`);
 154→
 155→          // Check if this is a plan upgrade by comparing old and new plan_id
 156→          const isUpgrade = existingUserSub.plan_id !== planData.id;
 157→          
 158→          if (isUpgrade) {
 159→            // Fetch old plan details to verify it's an upgrade (not downgrade)
 160→            const { data: oldPlan } = await supabase
 161→              .from("subscription_plans")
 162→              .select("name, monthly_document_limit, active_document_limit")
 163→              .eq("id", existingUserSub.plan_id)
 164→              .single();
 165→
 166→            const { data: newPlan } = await supabase
 167→              .from("subscription_plans")
 168→              .select("name, monthly_document_limit, active_document_limit")
 169→              .eq("id", planData.id)
 170→              .single();
 171→
 172→            console.log(`[subscription] Plan change detected: ${oldPlan?.name} → ${newPlan?.name}`);
 173→
 174→            // Reset monthly usage if upgrading to a higher tier (higher limits)
 175→            if (oldPlan && newPlan && 
 176→                (newPlan.monthly_document_limit > oldPlan.monthly_document_limit ||
 177→                 newPlan.active_document_limit > oldPlan.active_document_limit)) {
 178→              
 179→              console.log(`[subscription] Plan upgrade detected! Resetting monthly usage for user ${customerData.user_id}`);
 180→
 181→              const currentMonth = new Date().toISOString().slice(0, 7); // YYYY-MM format
 182→              
 183→              const { error: resetError } = await supabase
 184→                .from("monthly_usage")
 185→                .update({
 186→                  documents_created: 0,
 187→                  published_completed_count: 0
 188→                })
 189→                .eq("user_id", customerData.user_id)
 190→                .eq("year_month", currentMonth);
 191→
 192→              if (resetError) {
 193→                console.error("[subscription] Failed to reset monthly usage:", resetError);
 194→              } else {
 195→                console.log(`[subscription] ✅ Successfully reset monthly usage for ${currentMonth}`);
 196→              }
 197→            }
 198→          }
 199→
 200→          const { data: updatedSub, error: updateError } = await supabase
 201→            .from("subscriptions")
 202→            .update({
 203→              plan_id: planData.id,
 204→              status: finalStatus,
 205→              paddle_subscription_id: eventData.data.id,
 206→              paddle_customer_id: eventData.data.customerId,
 207→              paddle_price_id: priceId,
 208→              payment_provider: "paddle",
 209→              ends_at: endsAt,
 210→              updated_at: new Date().toISOString(),
 211→            })
 212→            .eq("id", existingUserSub.id)
 213→            .select("id")
 214→            .single();
 215→
 216→          if (updateError) {
 217→            console.error("[subscription] Failed to update subscription:", updateError);
 218→            throw updateError;
 219→          }
 220→
 221→          subscriptionId = updatedSub!.id;
 222→          console.log(`[subscription] Updated subscription to ${planName}: ${subscriptionId}`);
 223→        } else {
 224→          // user는 있지만 subscription이 없는 경우 (정상적이지 않은 상황)
 225→          console.log(`[subscription] User has no subscription, creating new one`);
 226→
 227→          const { data: newSub, error: insertError } = await supabase
 228→            .from("subscriptions")
 229→            .insert({
 230→              user_id: customerData.user_id,
 231→              plan_id: planData.id,
 232→              status: finalStatus,
 233→              paddle_subscription_id: eventData.data.id,
 234→              paddle_customer_id: eventData.data.customerId,
 235→              paddle_price_id: priceId,
 236→              payment_provider: "paddle",
 237→              starts_at: new Date().toISOString(),
 238→              ends_at: endsAt,
 239→            })
 240→            .select("id")
 241→            .single();
 242→
 243→          if (insertError) {
 244→            console.error("[subscription] Failed to create subscription:", insertError);
 245→            throw insertError;
 246→          }
 247→
 248→          subscriptionId = newSub!.id;
 249→          console.log(`[subscription] Created subscription: ${subscriptionId}`);
 250→        }
 251→      } else {
 252→        // user_id가 없으면 일단 subscription만 생성 (transaction.completed에서 연결됨)
 253→        console.log(`[subscription] No user linked yet, creating unlinked subscription`);
 254→
 255→        const { data: newSub, error: insertError } = await supabase
 256→          .from("subscriptions")
 257→          .insert({
 258→            user_id: null,
 259→            plan_id: planData.id,
 260→            status: finalStatus,
 261→            paddle_subscription_id: eventData.data.id,
 262→            paddle_customer_id: eventData.data.customerId,
 263→            paddle_price_id: priceId,
 264→            payment_provider: "paddle",
 265→            starts_at: new Date().toISOString(),
 266→            ends_at: endsAt,
 267→          })
 268→          .select("id")
 269→          .single();
 270→
 271→        if (insertError) {
 272→          console.error("[subscription] Failed to create subscription:", insertError);
 273→          throw insertError;
 274→        }
 275→
 276→        subscriptionId = newSub!.id;
 277→        console.log(`[subscription] Created unlinked subscription: ${subscriptionId}`);
 278→      }
 279→
 280→      // 5. users.current_subscription_id 업데이트 (user_id가 있을 때만)
 281→      if (customerData?.user_id) {
 282→        const { error: userUpdateError } = await supabase
 283→          .from("users")
 284→          .update({ current_subscription_id: subscriptionId })
 285→          .eq("id", customerData.user_id);
 286→
 287→        if (userUpdateError) {
 288→          console.error(
 289→            "Failed to update user's current_subscription_id:",
 290→            userUpdateError
 291→          );
 292→        }
 293→
 294→        console.log(
 295→          `Updated user ${customerData.user_id} subscription to ${planName} (${finalStatus})`
 296→        );
 297→      } else {
 298→        console.log(
 299→          `Created subscription ${subscriptionId} for ${planName} (${finalStatus}) - waiting for transaction.completed to link to user`
 300→        );
 301→      }
 302→    } catch (error) {
 303→      console.error("Error processing subscription webhook:", error);
 304→      throw error;
 305→    }
 306→  }
 307→
 308→  /**
 309→   * Handle customer.created/updated events
 310→   * Note: This creates/updates customer records but is NOT the primary linking mechanism.
 311→   * transaction.completed is the main event that links customers/subscriptions to users.
 312→   * This event may not fire for existing customers, so don't depend on it for critical flow.
 313→   */
 314→  private async updateCustomerData(
 315→    eventData: CustomerCreatedEvent | CustomerUpdatedEvent
 316→  ) {
 317→    const supabase = createServiceSupabase();
 318→
 319→    try {
 320→      console.log(
 321→        `[customer.${
 322→          eventData.eventType.includes("created") ? "created" : "updated"
 323→        }] Processing customer ${eventData.data.id} with email ${
 324→          eventData.data.email
 325→        }`
 326→      );
 327→
 328→      // 이메일로 사용자 찾기 (Supabase Auth Admin API 사용)
 329→      const {
 330→        data: { users },
 331→        error: userError,
 332→      } = await supabase.auth.admin.listUsers();
 333→      const user = users?.find((u) => u.email === eventData.data.email);
 334→
 335→      const { error } = await supabase
 336→        .from("customers")
 337→        .upsert({
 338→          customer_id: eventData.data.id,
 339→          email: eventData.data.email,
 340→          user_id: user?.id ?? null,
 341→        })
 342→        .select();
 343→
 344→      if (error) {
 345→        console.error("[customer] Failed to upsert customer:", error);
 346→        throw error;
 347→      }
 348→
 349→      console.log(
 350→        `[customer] Upserted customer: ${eventData.data.id} with email ${
 351→          eventData.data.email
 352→        }, user: ${user?.id || "not found"}`
 353→      );
 354→
 355→      // user_id가 있으면, 이 customer의 subscription을 찾아서 user_id 연결 (보조적 역할)
 356→      if (user?.id) {
 357→        // paddle_customer_id로 정확하게 이 customer의 subscription 찾기
 358→        const { data: subscriptions } = await supabase
 359→          .from("subscriptions")
 360→          .select("id, paddle_subscription_id, status")
 361→          .eq("paddle_customer_id", eventData.data.id)
 362→          .is("user_id", null);
 363→
 364→        if (subscriptions && subscriptions.length > 0) {
 365→          console.log(
 366→            `[customer] Found ${subscriptions.length} unlinked subscription(s) for customer ${eventData.data.id} - linking to user ${user.id}`
 367→          );
 368→
 369→          for (const sub of subscriptions) {
 370→            // subscription의 user_id 업데이트
 371→            const { error: subUpdateError } = await supabase
 372→              .from("subscriptions")
 373→              .update({
