 709→  /**
 710→   * Paddle Price ID로부터 플랜 이름 결정
 711→   * pricing-config.ts의 PADDLE_PRICE_TIERS를 단일 소스로 사용
 712→   */
 713→  private determinePlanFromPriceId(priceId: string | undefined): string {
 714→    if (!priceId) return "Basic";
 715→
 716→    // PADDLE_PRICE_TIERS를 순회하며 매칭
 717→    for (const tier of PADDLE_PRICE_TIERS) {
 718→      if (
 719→        tier.priceId.month === priceId ||
 720→        tier.priceId.year === priceId ||
 721→        tier.priceId.monthNoTrial === priceId ||
 722→        tier.priceId.yearNoTrial === priceId
 723→      ) {
 724→        return tier.name;
 725→      }
 726→    }
 727→
 728→    // 매칭 실패 시 기본값
 729→    return "Basic";
 730→  }
 731→
 732→  /**
 733→   * 주어진 priceId가 무료체험이 포함된 가격인지 확인
 734→   * (무료체험 없는 버전이 존재하는 경우 = 무료체험 제공 플랜)
 735→   */
 736→  private isPriceWithTrial(priceId: string | undefined): boolean {
 737→    if (!priceId) return false;
 738→
 739→    for (const tier of PADDLE_PRICE_TIERS) {
 740→      // trial 버전 priceId인지 확인 (noTrial이 아닌 원본)
 741→      if (tier.priceId.month === priceId || tier.priceId.year === priceId) {
 742→        // 이 tier가 noTrial 버전을 가지고 있다면 = trial 제공 플랜
 743→        return !!(tier.priceId.monthNoTrial || tier.priceId.yearNoTrial);
 744→      }
 745→    }
 746→
 747→    return false;
 748→  }
