 785→  /**
 786→   * Paddle 구독 상태를 SeukSeuk 상태로 매핑
 787→   */
 788→  private mapPaddleStatus(paddleStatus: string): string {
 789→    switch (paddleStatus) {
 790→      case "active":
 791→        return "active";
 792→      case "canceled":
 793→      case "past_due":
 794→        return "canceled";
 795→      case "paused":
 796→        return "canceled"; // SeukSeuk에 paused 상태가 없다면 canceled로 처리
 797→      case "trialing":
 798→        return "active"; // 트라이얼 기간도 active로 처리
 799→      default:
 800→        return "canceled";
 801→    }
 802→  }
