-- Paddle 고객 테이블
CREATE TABLE public.customers (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  customer_id text UNIQUE NOT NULL,
  email text NOT NULL,
  user_id uuid REFERENCES auth.users(id) ON DELETE SET NULL,
  has_used_free_trial boolean DEFAULT false,
  first_trial_date timestamptz,
  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now()
);

-- 구독 테이블
CREATE TABLE public.subscriptions (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id uuid REFERENCES auth.users(id) ON DELETE SET NULL,
  paddle_subscription_id text UNIQUE,
  paddle_customer_id text,
  paddle_price_id text,
  status text NOT NULL DEFAULT 'inactive',
  starts_at timestamptz,
  ends_at timestamptz,
  canceled_at timestamptz,
  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now()
);

-- 웹훅 이벤트 로그 (감사 추적용)
CREATE TABLE public.webhook_events (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  event_type text NOT NULL,
  paddle_event_id text,
  payload jsonb,
  processed_at timestamptz DEFAULT now(),
  status text DEFAULT 'processed',
  error_message text
);

-- 인덱스
CREATE INDEX idx_customers_email ON public.customers(email);
CREATE INDEX idx_customers_user_id ON public.customers(user_id);
CREATE INDEX idx_subscriptions_user_id ON public.subscriptions(user_id);
CREATE INDEX idx_subscriptions_paddle_customer_id ON public.subscriptions(paddle_customer_id);
CREATE INDEX idx_subscriptions_status ON public.subscriptions(status);
CREATE INDEX idx_webhook_events_event_type ON public.webhook_events(event_type);

-- RLS 활성화
ALTER TABLE public.customers ENABLE ROW LEVEL SECURITY;
ALTER TABLE public.subscriptions ENABLE ROW LEVEL SECURITY;
ALTER TABLE public.webhook_events ENABLE ROW LEVEL SECURITY;

-- customers RLS 정책
CREATE POLICY "Users can read own customer data" ON public.customers
  FOR SELECT USING (auth.uid() = user_id);

-- subscriptions RLS 정책
CREATE POLICY "Users can read own subscriptions" ON public.subscriptions
  FOR SELECT USING (auth.uid() = user_id);

-- webhook_events는 서비스 역할로만 접근 (RLS 정책 없음 = 일반 유저 접근 불가)

-- 이메일로 유저 ID를 찾는 함수 (listUsers() 대체)
CREATE OR REPLACE FUNCTION public.get_user_id_by_email(lookup_email text)
RETURNS uuid
LANGUAGE sql
SECURITY DEFINER
SET search_path = ''
AS $$
  SELECT id FROM auth.users WHERE email = lookup_email LIMIT 1;
$$;

