    "API 사용량 대시보드",
    "99.9% Uptime SLA",
  ],
} as const;
```

### 3. `lib/paddle/get-customer-id.ts`
Get Paddle customer ID from current authenticated user. The server Supabase client is at `@/lib/supabase/server` and exports `createClient()` (async function that returns a Supabase client).

```typescript
"use server";

import { createClient } from "@/lib/supabase/server";

export async function getCustomerId(): Promise<string> {
  const supabase = await createClient();
  const { data: { user } } = await supabase.auth.getUser();

  if (user?.email) {
    const { data: customerData } = await supabase
      .from("customers")
      .select("customer_id")
      .eq("email", user.email)
      .single();

    if (customerData?.customer_id) {
      return customerData.customer_id;
    }
  }
  return "";
}
```

### 4. `lib/paddle/get-subscription.ts`
Server action to get subscription for current user.

```typescript
"use server";

import { createClient } from "@/lib/supabase/server";

export async function getSubscription() {
  const supabase = await createClient();
  const { data: { user } } = await supabase.auth.getUser();

  if (!user) return null;

  const { data, error } = await supabase
    .from("subscriptions")
    .select("*")
    .eq("user_id", user.id)
    .eq("status", "active")
    .order("created_at", { ascending: false })
    .limit(1)
    .single();

  if (error || !data) return null;
  return data;
}
```

### 5. `lib/paddle/cancel-subscription.ts`
Server action to cancel a subscription. Must verify ownership.

```typescript
"use server";

import { createClient } from "@/lib/supabase/server";
import { getPaddleInstance } from "./get-paddle-instance";

export async function cancelSubscription(subscriptionId: string) {
  const supabase = await createClient();
  const { data: { user } } = await supabase.auth.getUser();

  if (!user) {
    return { error: "Unauthorized" };
  }

  // Verify ownership
  const { data: subscription } = await supabase
    .from("subscriptions")
    .select("paddle_subscription_id")
    .eq("id", subscriptionId)
    .eq("user_id", user.id)
    .single();

  if (!subscription?.paddle_subscription_id) {
    return { error: "Subscription not found" };
  }

  try {
    const paddle = getPaddleInstance();
    await paddle.subscriptions.cancel(subscription.paddle_subscription_id, {
      effectiveFrom: "next_billing_period",
    });

    await supabase
      .from("subscriptions")
      .update({
        canceled_at: new Date().toISOString(),
        updated_at: new Date().toISOString(),
      })
      .eq("id", subscriptionId);

    return { success: true };
  } catch (error) {
    console.error("Failed to cancel subscription:", error);
    return { error: "Failed to cancel subscription" };
  }
}
```

