import { EventEntity, EventName } from "@paddle/paddle-node-sdk";
import { createClient as createSupabaseClient } from "@supabase/supabase-js";
import { getPaddleInstance } from "./get-paddle-instance";

function createServiceClient() {
  return createSupabaseClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.SUPABASE_SERVICE_ROLE_KEY!
  );
}

async function logWebhookEvent(
  eventType: string,
  payload: unknown,
  status: "processed" | "error" = "processed",
  errorMessage?: string
) {
  try {
    const supabase = createServiceClient();
    await supabase.from("webhook_events").insert({
      event_type: eventType,
      payload: payload as any,
      status,
      error_message: errorMessage,
    });
  } catch (e) {
    console.error("[webhook-log] Failed to log event:", e);
  }
}

export async function processWebhookEvent(eventData: EventEntity) {
  const eventType = eventData.eventType;
  console.log(`[webhook] Processing: ${eventType}`);

  try {
    switch (eventType) {
      case EventName.SubscriptionCreated:
      case EventName.SubscriptionUpdated:
      case EventName.SubscriptionCanceled:
        await handleSubscriptionEvent(eventData);
        break;
      case EventName.CustomerCreated:
      case EventName.CustomerUpdated:
        await handleCustomerEvent(eventData);
        break;
      case EventName.TransactionCompleted:
        await handleTransactionCompleted(eventData);
        break;
      default:
        console.log(`[webhook] Unhandled event: ${eventType}`);
    }
    await logWebhookEvent(eventType, eventData.data);
  } catch (error) {
    await logWebhookEvent(eventType, eventData.data, "error", error instanceof Error ? error.message : "Unknown");
    throw error; // Re-throw so webhook route returns 500 → Paddle retries
  }
}

function mapPaddleStatus(paddleStatus: string): string {
  switch (paddleStatus) {
    case "active":
    case "trialing":
    case "past_due": // FIX: Keep as active during payment retry period
      return "active";
    case "canceled":
      return "canceled";
    case "paused":
      return "paused";
    default:
      return "inactive";
  }
}

async function handleSubscriptionEvent(eventData: EventEntity) {
  const supabase = createServiceClient();
  const data = eventData.data as any;
  const paddleSubscriptionId = data.id;
  const paddleCustomerId = data.customerId;
  const priceId = data.items?.[0]?.price?.id;
  const status = mapPaddleStatus(data.status);

  console.log(`[subscription] ${eventData.eventType}: ${paddleSubscriptionId} customer=${paddleCustomerId} status=${status}`);

  // Calculate ends_at
  let endsAt: string | null = null;
  if (data.scheduled_change?.action === "cancel") {
    endsAt = data.scheduled_change.effective_at || null;
  } else if (data.next_billed_at) {
    endsAt = data.next_billed_at;
  } else if (data.currentBillingPeriod?.endsAt) {
    endsAt = data.currentBillingPeriod.endsAt;
  }

  // Try to find user_id from customers table
  const { data: customerData } = await supabase
    .from("customers")
    .select("user_id")
    .eq("customer_id", paddleCustomerId)
    .single();

  const userId = customerData?.user_id || null;

  // IDEMPOTENT UPSERT on paddle_subscription_id
  const { error } = await supabase
    .from("subscriptions")
    .upsert(
      {
        user_id: userId,
        paddle_subscription_id: paddleSubscriptionId,
        paddle_customer_id: paddleCustomerId,
        paddle_price_id: priceId,
        status,
        starts_at: data.startedAt || new Date().toISOString(),
        ends_at: endsAt,
        canceled_at: status === "canceled" ? new Date().toISOString() : null,
        updated_at: new Date().toISOString(),
      },
      { onConflict: "paddle_subscription_id" }
    );

  if (error) {
    console.error("[subscription] Upsert failed:", error);
    throw error;
  }

  console.log(`[subscription] Upserted: ${paddleSubscriptionId} status=${status} user=${userId || "unlinked"}`);
}

async function handleCustomerEvent(eventData: EventEntity) {
  const supabase = createServiceClient();
  const data = eventData.data as any;
  const customerId = data.id;
  const email = data.email;

  console.log(`[customer] ${eventData.eventType}: ${customerId} email=${email}`);

  // FIX: Use RPC function instead of listUsers()
  const { data: userId } = await supabase.rpc("get_user_id_by_email", {
    lookup_email: email,
  });

  // Upsert customer record
  const { error } = await supabase
    .from("customers")
    .upsert(
      {
        customer_id: customerId,
        email,
        user_id: userId || null,
        updated_at: new Date().toISOString(),
      },
      { onConflict: "customer_id" }
    );

  if (error) {
    console.error("[customer] Upsert failed:", error);
    throw error;
  }

  // If user found, link any orphaned subscriptions
  if (userId) {
    const { data: orphanedSubs } = await supabase
      .from("subscriptions")
      .select("id, status")
      .eq("paddle_customer_id", customerId)
      .is("user_id", null);

    if (orphanedSubs && orphanedSubs.length > 0) {
      console.log(`[customer] Linking ${orphanedSubs.length} orphaned subscription(s) to user ${userId}`);
      for (const sub of orphanedSubs) {
        await supabase
          .from("subscriptions")
          .update({ user_id: userId, updated_at: new Date().toISOString() })
          .eq("id", sub.id);
      }
    }
  }

  console.log(`[customer] Upserted: ${customerId} user=${userId || "not found"}`);
}

async function handleTransactionCompleted(eventData: EventEntity) {
  const supabase = createServiceClient();
