      .eq("customer_id", customerId)
      .single();
    customerEmail = dbCustomer?.email || null;
  }

  if (!customerEmail) {
    console.error(`[transaction.completed] No email for customer ${customerId}`);
    throw new Error(`No email found for customer ${customerId}`); // FIX: throw instead of silent return
  }

  // 2. Upsert customer record
  // FIX: Use RPC instead of listUsers()
  const { data: userId } = await supabase.rpc("get_user_id_by_email", {
    lookup_email: customerEmail,
  });

  const { error: customerUpsertError } = await supabase
    .from("customers")
    .upsert(
      {
        customer_id: customerId,
        email: customerEmail,
        user_id: userId || null,
        updated_at: new Date().toISOString(),
      },
      { onConflict: "customer_id" }
    );

  if (customerUpsertError) {
    console.error("[transaction.completed] Customer upsert failed:", customerUpsertError);
    throw customerUpsertError; // FIX: throw error
  }

