      .from("subscriptions")
      .select("id, paddle_subscription_id, status")
      .eq("paddle_customer_id", customerId)
      .is("user_id", null);

    if (orphanedSubs && orphanedSubs.length > 0) {
      console.log(`[transaction.completed] Linking ${orphanedSubs.length} subscription(s) to user ${userId}`);

      for (const sub of orphanedSubs) {
        const { error: linkError } = await supabase
          .from("subscriptions")
          .update({ user_id: userId, updated_at: new Date().toISOString() })
          .eq("id", sub.id);

        if (linkError) {
          console.error(`[transaction.completed] Failed to link subscription ${sub.id}:`, linkError);
