import { Calendar, Code2, Layers, Zap } from "lucide-react"

const features = [
  {
    icon: Calendar,
    title: "공휴일 조회",
    description: "대한민국 법정 공휴일, 대체 공휴일, 임시 공휴일까지 모두 정확하게 조회할 수 있습니다.",
  },
  {
    icon: Layers,
    title: "커스텀 공휴일",
    description: "회사 창립기념일, 사내 행사일 등 나만의 커스텀 공휴일을 등록하고 API로 조회하세요.",
  },
  {
    icon: Zap,
    title: "빠른 응답 속도",
    description: "어디서나 100ms 이내 응답. 안정적인 인프라로 서비스합니다.",
  },
  {
    icon: Code2,
    title: "개발자 친화적",
    description: "RESTful API, JSON 응답, MCP 지원. 어떤 언어에서든, AI Agent에서도 쉽게 연동할 수 있습니다.",
  },
]

export function FeaturesSection() {
  return (
    <section id="features" className="px-6 py-24">
      <div className="mx-auto max-w-7xl">
        <div className="mb-16 text-center">
          <p className="mb-3 text-sm font-medium uppercase tracking-widest text-primary">Features</p>
          <h2 className="text-balance text-3xl font-bold tracking-tight text-foreground md:text-4xl">
            {"개발자를 위해 설계된 API"}
          </h2>
          <p className="mx-auto mt-4 max-w-xl text-pretty text-muted-foreground">
            {"단순한 공휴일 조회를 넘어, 비즈니스에 필요한 모든 휴일 관련 기능을 제공합니다."}
          </p>
        </div>

        <div className="grid gap-6 md:grid-cols-2">
          {features.map((feature) => (
            <div
              key={feature.title}
              className="group rounded-xl border border-border bg-card p-6 transition-colors hover:border-primary/30 hover:bg-card/80"
            >
              <div className="mb-4 flex h-10 w-10 items-center justify-center rounded-lg bg-primary/10">
                <feature.icon className="h-5 w-5 text-primary" />
              </div>
              <h3 className="mb-2 text-lg font-semibold text-foreground">{feature.title}</h3>
              <p className="text-sm leading-relaxed text-muted-foreground">{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  )
}

"use client"

import { useState, useEffect, useCallback } from "react"
import { Card, CardContent } from "@/components/ui/card"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { Copy, Cpu } from "lucide-react"
import { createClient } from "@/lib/supabase/client"
import { toast } from "sonner"

interface ApiKey {
  id: string
  key: string
  is_active: boolean
}

export default function McpPage() {
  const [keys, setKeys] = useState<ApiKey[]>([])
  const [loading, setLoading] = useState(true)

  const fetchKeys = useCallback(async () => {
    const supabase = createClient()
    const { data } = await supabase
      .from("api_keys")
      .select("id, key, is_active")
      .order("created_at", { ascending: false })
    setKeys(data || [])
    setLoading(false)
  }, [])

  useEffect(() => {
    fetchKeys()
  }, [fetchKeys])

  const apiKey = keys.length > 0 ? keys[0].key : "YOUR_API_KEY"

  const copyToClipboard = async (text: string, label: string) => {
    await navigator.clipboard.writeText(text)
    toast.success(`${label} 복사되었습니다.`)
  }

  return (
    <div className="mx-auto max-w-6xl">
      <div className="mb-8">
        <h1 className="text-2xl font-bold text-foreground">MCP</h1>
        <p className="mt-1 text-sm text-muted-foreground">
          AI Agent에서 HuDy API를 바로 사용하세요.
        </p>
      </div>

      {/* 설정 가이드 */}
      <Card className="mb-6">
        <CardContent className="pt-6">
          <div className="flex items-start gap-3 mb-6">
            <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-primary/10">
              <Cpu className="h-5 w-5 text-primary" />
            </div>
            <div>
              <h2 className="text-lg font-semibold text-foreground">설정 가이드</h2>
              <p className="mt-1 text-sm text-muted-foreground">
                MCP를 지원하는 AI 도구에서 아래 설정을 추가하세요.
              </p>
            </div>
          </div>

          {loading ? (
            <div className="h-32 animate-pulse rounded-lg bg-muted" />
          ) : (
            <Tabs defaultValue="claude-code" className="w-full">
              <TabsList className="grid w-full grid-cols-3 mb-3">
                <TabsTrigger value="claude-code">Claude Code</TabsTrigger>
                <TabsTrigger value="claude-desktop">Claude Desktop</TabsTrigger>
                <TabsTrigger value="cursor">Cursor</TabsTrigger>
              </TabsList>

              <TabsContent value="claude-code">
                <p className="mb-2 text-xs text-muted-foreground">터미널에서 아래 명령어를 실행하세요.</p>
                <div className="relative rounded-lg border border-border bg-muted/30">
                  <pre className="overflow-x-auto p-4 text-xs">
                    <code className="font-mono text-foreground">
{`claude mcp add --transport http hudy https://www.hudy.co.kr/api/mcp -H "x-api-key: ${apiKey}"`}
                    </code>
                  </pre>
                  <button
                    type="button"
                    onClick={() => copyToClipboard(
                      `claude mcp add --transport http hudy https://www.hudy.co.kr/api/mcp -H "x-api-key: ${apiKey}"`,
                      "Claude Code 명령어가"
                    )}
                    className="absolute right-2 top-2 rounded-md p-2 text-muted-foreground transition-colors hover:bg-secondary hover:text-foreground"
                    aria-label="명령어 복사"
                  >
                    <Copy className="h-4 w-4" />
                  </button>
                </div>
              </TabsContent>

              <TabsContent value="claude-desktop">
                <div className="relative rounded-lg border border-border bg-muted/30">
                  <pre className="overflow-x-auto p-4 text-xs">
                    <code className="font-mono text-foreground">
{`{
  "mcpServers": {
    "hudy": {
      "url": "https://www.hudy.co.kr/api/mcp",
      "headers": {
        "x-api-key": "${apiKey}"
      }
    }
  }
}`}
                    </code>
                  </pre>
                  <button
                    type="button"
                    onClick={() => copyToClipboard(
                      JSON.stringify({
                        mcpServers: {
                          hudy: {
                            url: "https://www.hudy.co.kr/api/mcp",
                            headers: { "x-api-key": apiKey }
                          }
                        }
                      }, null, 2),
                      "Claude Desktop 설정이"
                    )}
                    className="absolute right-2 top-2 rounded-md p-2 text-muted-foreground transition-colors hover:bg-secondary hover:text-foreground"
                    aria-label="설정 복사"
                  >
                    <Copy className="h-4 w-4" />
                  </button>
                </div>
              </TabsContent>

              <TabsContent value="cursor">
                <div className="relative rounded-lg border border-border bg-muted/30">
                  <pre className="overflow-x-auto p-4 text-xs">
                    <code className="font-mono text-foreground">
{`{
  "mcpServers": {
    "hudy": {
      "url": "https://www.hudy.co.kr/api/mcp",
      "headers": {
        "x-api-key": "${apiKey}"
      }
    }
  }
}`}
                    </code>
                  </pre>
                  <button
                    type="button"
                    onClick={() => copyToClipboard(
                      JSON.stringify({
                        mcpServers: {
                          hudy: {
                            url: "https://www.hudy.co.kr/api/mcp",
                            headers: { "x-api-key": apiKey }
                          }
                        }
                      }, null, 2),
                      "Cursor 설정이"
                    )}
                    className="absolute right-2 top-2 rounded-md p-2 text-muted-foreground transition-colors hover:bg-secondary hover:text-foreground"
                    aria-label="설정 복사"
                  >
                    <Copy className="h-4 w-4" />
                  </button>
                </div>
              </TabsContent>
            </Tabs>
          )}
        </CardContent>
      </Card>

      {/* 사용 가능한 도구 */}
      <Card>
        <CardContent className="pt-6">
          <h2 className="mb-4 text-lg font-semibold text-foreground">사용 가능한 도구</h2>
          <div className="grid gap-2 sm:grid-cols-2">
            {[
              { name: "get_holidays", desc: "공휴일 조회" },
              { name: "count_business_days", desc: "영업일 수 계산" },
              { name: "add_business_days", desc: "영업일 더하기" },
              { name: "subtract_business_days", desc: "영업일 빼기" },
              { name: "check_business_day", desc: "영업일 여부 확인" },
              { name: "list_custom_holidays", desc: "커스텀 공휴일 목록" },
              { name: "create_custom_holiday", desc: "커스텀 공휴일 생성" },
              { name: "update_custom_holiday", desc: "커스텀 공휴일 수정" },
              { name: "delete_custom_holiday", desc: "커스텀 공휴일 삭제" },
            ].map((tool) => (
              <div key={tool.name} className="flex items-start gap-2 text-sm">
                <span className="text-primary">•</span>
                <div>
                  <code className="font-mono text-foreground">{tool.name}</code>
                  <span className="text-muted-foreground"> — {tool.desc}</span>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

